with
    t10 as
        (
            select
                '' as data_base64
            from dual
        ),
    t20 as
        (
            select
                TO_BLOB(utl_encode.BASE64_DECODE(UTL_RAW.CAST_TO_RAW(t.data_base64))) as data_as_blob
            from t10 t
        )
select
    *
from
    t20,
    t10
