with
    t10 as
        (
            select
                'test' as text_value,
                'iVBORw0KGgoAAAANSUhEUgAAAAgAAAAIAQMAAAD+wSzIAAAABlBMVEX///+/v7+jQ3Y5AAAADklEQVQI12P4AIX8EAgALgAD/aNpbtEAAAAASUVORK5CYII' as gif_base64,
                '' as jpg_base64
            from dual
        ),
    t20 as
        (
            select
                UTL_RAW.CAST_TO_RAW(t.text_value) as text_raw,
                UTL_RAW.CAST_TO_RAW(t.gif_base64) as gif_base64_raw,
                TO_BLOB(utl_encode.BASE64_DECODE(UTL_RAW.CAST_TO_RAW(t.jpg_base64))) as jpg_blob
            from t10 t
        ),
    t30 as(
        select
            utl_encode.BASE64_ENCODE(t.text_raw) as text_base64,
            UTL_RAW.CAST_TO_VARCHAR2(t.text_raw) as text_from_raw,
            UTL_RAW.CAST_TO_VARCHAR2(t.gif_base64_raw) as gif_base64_from_raw,
            utl_encode.BASE64_DECODE(t.gif_base64_raw) as gif_bin_raw
        from t20 t
    ),
    t25 as (
        select
            TO_BLOB(t.gif_bin_raw) as gif_blob
        from t30 t
    ),
    t40 as (
        select
            utl_encode.BASE64_DECODE(t.text_base64) as raw_from_base64
        from t30 t
    ),
    t50 as (
        select
            UTL_RAW.CAST_TO_VARCHAR2(t.raw_from_base64) as text_from_base64,
            TO_BLOB(t.raw_from_base64) as blob_from_base64 -- oracle 19
        from t40 t--, t20
    )
select
    *
    --t20.jpg_blob
from
    --t10,
    t20,
    t25,
    t30,
    t40,
    t50
;